mapper.delete:
delete from pgsds.section_units
where 1 = 1
and section_id = #{section_id}

delete from pgsds.section_units
where 1 = 1
and section_id = #{section_id}
and unit_id = #{unit_id}

mapper.insert:
insert into pgsds.section_units (
      section_id
    , unit_id
    , createdAt
    , updatedAt
) value (
      #{section_id}
    , #{unit_id}
    , #{createdAt}
    , #{updatedAt}
)
