mapper.insert:
insert into pgsds.sections (
      id
    , name
    , createdAt
    , updatedAt
) value (
      #{id}
    , #{name}
    , #{createdAt}
    , #{updatedAt}
)

mapper.update:
update pgsds.sections
    set name =  #{name}
    , updatedAt = #{updatedAt}
where 1 = 1
and id = #{id}

mapper.delete:
delete from pgsds.sections
where 1 = 1
and id = #{id}

mapper.select:
select name from pgsds.sections where 1 = 1 and id in (
#{item}
)
