mapper.insert:
insert into pgsds.units (
      id
    , name
    , type
    , incoming_time
    , car_no
    , enabled
    , createdAt
    , updatedAt
) value (
      #{id}
    , #{name}
    , #{type}
    , #{incoming_time}
    , #{car_no}
    , #{enabled}
    , #{createdAt}
    , #{updatedAt}
)

mapper.update:
update pgsds.units
    set name =  #{name}
    , type = #{type}
    , incoming_time = #{incoming_time}
    , car_no = #{car_no}
    , enabled = #{enabled}
    , updatedAt = #{updatedAt}
where 1 = 1
and id = #{id}

mapper.delete:
delete from pgsds.units
where 1 = 1
and id = #{id}
