mapper.delete:
delete from pgsds.code_details
where 1 = 1
and code_id = #{id}
and id = #{sid}

mapper.update:
update pgsds.code_details
    set name =  #{name}
    , enabled = #{enabled}
    , sort = #{sort}
    , attr1 = #{attr1}
    , attr2 = #{attr2}
    , attr3 = #{attr3}
    , updatedAt = #{updatedAt}
where 1 = 1
and code_id = #{id}
and id = #{sid}

insert into pgsds.code_details (
      id
    , code_id
    , name
    , enabled
    , sort
    , attr1
    , attr2
    , attr3
    , createdAt
    , updatedAt
) value (
      #{sid}
    , #{id}
    , #{name}
    , #{enabled}
    , #{sort}
    , #{attr1}
    , #{attr2}
    , #{attr3}
    , #{createdAt}
    , #{updatedAt}
)

mapper.select:
select
    count(0) as cnt
from pgsds.code_details
where 1 = 1
and code_id = #{id}
and id = #{sid}
