mapper.delete:
delete from pgsds.codes
where 1 = 1
and id = #{id}

mapper.update:
update pgsds.codes
    set name =  #{name}
    , enabled = #{enabled}
    , updatedAt = #{updatedAt}
where 1 = 1
and id = #{id}

insert into pgsds.codes (
      id
    , name
    , enabled
    , createdAt
    , updatedAt
) value (
      #{id}
    , #{name}
    , #{enabled}
    , #{createdAt}
    , #{updatedAt}
)
