mapper.delete:
delete from pgsds.users
where 1 = 1
and username = #{username}

delete from pgsds.user_details
where 1 = 1
and username = #{username}

delete from pgsds.authorities
where 1 = 1
and username = #{username}

mapper.update:
insert into pgsds.users (
      username
    , password
    , enabled
) value (
      #{username}
    , '1'
    , #{enabled}
)

insert into pgsds.user_details (
      username
    , fullname
    , phone
    , createdAt
) value (
      #{username}
    , #{fullname}
    , #{phone}
    , #{createdAt}
)

insert into pgsds.authorities (
      username
    , authority
) value (
      #{username}
    , #{authority}
)

update pgsds.users
    set enabled = #{enabled}
where 1 = 1
and username = #{username}

update pgsds.user_details
    set fullname = #{fullname}
    , phone = #{phone}
where 1 = 1
and username = #{username}
