mapper.insert:
insert into pgsds.csct (
    created_At
    , site_id
    , site_type
    , title
    , contents
    , created_by
) value (
      #{created_at}
    , #{site_id}
    , #{site_type}
    , #{title}
    , #{contents}
    , #{created_by}
)

mapper.update:
update pgsds.csct
    set site_id =  #{site_id}
    , site_type = #{site_type}
    , title = #{title}
    , contents = #{contents}   
where 1 = 1
and created_by = #{created_by}

mapper.delete:
delete from pgsds.csct
where 1 = 1
and call_id = #{call_id}
